<script type="text/javascript">
 
    var total = 0;
    var tot = 0;
 
    var item = null;
    var price = 0;
 
 
    var a = "";
 
    $("img").on("click",function()
        {
 
            a = $(this).prop('id')
 
        });
 
 
    function add()
    {
        if(a == "Black ")
        {
            item = "Black current";
            price = 30;
        }
        else if(a == "Mango")
        {
            item = "Mango";
            price = 30;
        }
        else if(a == "custard")
        {
            item = "Custard Apple";
            price = 30;
        }
        else if(a == "chocolate")
        {
            item = "chocolate";
            price = 30;
        }
 
        else if(a == "MixFruit")
        {
            item = "MixFruit";
            price = 40;
        }
 
 else if(a == "badam")
        {
            item = "Badam Faluda";
            price = 50;
        }
 
 else if(a == "royal")
        {
            item = "Royal Faluda";
            price = 50;
        }
 
 else if(a == "kesar")
        {
            item = "Kesar Faluda";
            price = 50;
        }
 
 else if(a == "vanila")
        {
            item = "Vanila";
            price = 20;
        }
 
 else if(a == "pista")
        {
            item = "Pista Kulfi";
            price = 25;
        }
 
 else if(a == "malai")
        {
            item = "MalaiKulfi";
            price = 25;
        }
  else if(a == "kulfi")
        {
            item = "KesarKulfi";
            price = 25;
        }
 else if(a == "butter")
        {
            item = "butterscoth";
            price = 30;
        }
 
  else if(a == "oreo")
        {
            item = "oreo icecream";
            price = 30;
        }
 
 
 
 
        var qty = $('#qty').val();
        tot = qty * price;
 
        var table1 =
            "<tr>" +
            "<td><button type='button' name='record' class='btn btn-warning' onclick='deleterow(this)'>Delete</td>" +
            "<td>" + item    +  "</td>" +
            "<td>" + price    +  "</td>" +
            "<td>" + qty    +  "</td>" +
            "<td>" + tot    +  "</td>" +
            "</tr>" ;
 
 
        total += Number(tot);
        $('#total').val(total);
 
        $("table tbody").append(table1);
        $("exampleModal").modal('toggle');
        $('#qty').val("1");
    }
 
    function deleterow(e)
    {
        total_cost = parseInt($(e).parent().parent().find('td:last').text(),10);
        total -= total_cost;
        $('#total').val(total);
        $(e).parent().parent().remove();
 
    }
 
    $('#reset').click(function()
    {
        location.reload();
    });
</script>
